609|  // Helper function to create audit log - REMOVED loadAuditLogs from dependencies
610|  const createAuditLog = useCallback(async (
611|    entityType: FreightAuditLog['entityType'],
612|    entityId: string,
613|    action: FreightAuditLog['action'],
614|    changes: FreightAuditLog['changes'],
615|    entitySnapshot: Record<string, unknown>,
616|    version?: number
617|  ) => {
618|    if (!user) return;
619|
620|    try {
621|      const log = {
622|        entity_type: entityType,
623|        entity_id: entityId,
624|        action,
625|        changes,
626|        entity_snapshot: entitySnapshot,
627|        changed_by: user.id,
628|        changed_by_username: user.username,
629|        changed_by_name: user.name,
630|        version,
631|      };
