865|    const sameGroup = seaFreights.filter(f => 
866|      f.carrier === carrier && 
867|      f.pol === pol && 
868|      f.pod === pod &&
869|      f.id !== excludeId
870|    );
871|    if (sameGroup.length === 0) return 1;
872|    const maxVersion = Math.max(...sameGroup.map(f => f.version || 1));
873|    return maxVersion + 1;
874|  };
