2760|  // Time Machine functions
2761|  const getHistoricalSnapshot = (targetDate: string): HistoricalFreightSnapshot | null => {
2762|    const targetTime = new Date(targetDate).getTime();
2763|    
2764|    const reconstructEntity = <T extends Record<string, unknown>>(
2765|      entityType: FreightAuditLog['entityType'],
2766|      currentEntities: T[]
2767|    ): T[] => {
2768|      const relevantLogs = auditLogs
2769|        .filter(log => log.entityType === entityType && new Date(log.timestamp).getTime() <= targetTime)
2770|        .sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime());
2771|
2772|      const entityMap = new Map<string, T>();
2773|
2774|      currentEntities.forEach(entity => {
2775|        const createdAt = (entity as { createdAt?: string }).createdAt;
2776|        if (createdAt && new Date(createdAt).getTime() <= targetTime) {
2777|          entityMap.set((entity as { id: string }).id, entity);
2778|        }
2779|      });
2780|
2781|      relevantLogs.forEach(log => {
2782|        if (log.action === 'create') {
2783|          entityMap.set(log.entityId, log.entitySnapshot as T);
2784|        } else if (log.action === 'update') {
2785|          entityMap.set(log.entityId, log.entitySnapshot as T);
2786|        } else if (log.action === 'delete') {
2787|          entityMap.delete(log.entityId);
2788|        }
2789|      });
2790|
2791|      return Array.from(entityMap.values());
2792|    };
2793|
2794|    return {
2795|      date: targetDate,
2796|      seaFreights: reconstructEntity<SeaFreight>('seaFreight', seaFreights),
2797|      agentSeaFreights: reconstructEntity<AgentSeaFreight>('agentSeaFreight', agentSeaFreights),
2798|      dthcList: reconstructEntity<DTHC>('dthc', dthcList),
2799|      dpCosts: reconstructEntity<DPCost>('dpCost', dpCosts),
2800|      combinedFreights: reconstructEntity<CombinedFreight>('combinedFreight', combinedFreights),
2801|      portBorderFreights: reconstructEntity<PortBorderFreight>('portBorderFreight', portBorderFreights),
2802|      borderDestinationFreights: reconstructEntity<BorderDestinationFreight>('borderDestinationFreight', borderDestinationFreights),
2803|      weightSurchargeRules: reconstructEntity<WeightSurchargeRule>('weightSurcharge', weightSurchargeRules),
2804|    };
2805|  };
