2825|  // Cost Calculation with expired rate tracking
2826|  const calculateCost = (input: CostCalculationInput): CostCalculationResult | null => {
2827|    const calculationDate = input.historicalDate || new Date().toISOString().split('T')[0];
2828|    
2829|    let snapshot: HistoricalFreightSnapshot | null = null;
2830|    if (input.historicalDate) {
2831|      snapshot = getHistoricalSnapshot(input.historicalDate);
2832|      if (!snapshot) {
2833|        return null;
2834|      }
2835|    }
2836|
2837|    const getDataSource = <T,>(current: T[], historical: T[] | undefined): T[] => {
2838|      return snapshot && historical ? historical : current;
2839|    };
2840|
2841|    const currentSeaFreights = getDataSource(seaFreights, snapshot?.seaFreights);
2842|    const currentAgentSeaFreights = getDataSource(agentSeaFreights, snapshot?.agentSeaFreights);
2843|    const currentDthcList = getDataSource(dthcList, snapshot?.dthcList);
2844|    const currentDpCosts = getDataSource(dpCosts, snapshot?.dpCosts);
2845|    const currentCombinedFreights = getDataSource(combinedFreights, snapshot?.combinedFreights);
2846|    const currentPortBorderFreights = getDataSource(portBorderFreights, snapshot?.portBorderFreights);
2847|    const currentBorderDestinationFreights = getDataSource(borderDestinationFreights, snapshot?.borderDestinationFreights);
2848|    const currentWeightSurchargeRules = getDataSource(weightSurchargeRules, snapshot?.weightSurchargeRules);
2849|
2850|    // Helper functions that return both value and expiration status
2851|    const getDPCostWithExpiry = (port: string): { value: number; expired: boolean } => {
2852|      const filtered = currentDpCosts.filter((d) => d.port === port);
2853|      if (filtered.length === 0) return { value: 0, expired: false };
2854|      
2855|      const validDP = filtered.filter(d => isValidOnDate(d.validFrom, d.validTo, calculationDate));
2856|      if (validDP.length > 0) {
2857|        return { value: validDP[0].amount, expired: false };
2858|      }
2859|      
2860|      // Use expired rate if no valid one exists
2861|      return { value: filtered[0].amount, expired: true };
