609|  // Helper function to create audit log - REMOVED loadAuditLogs from dependencies
610|  const createAuditLog = useCallback(async (
611|    entityType: FreightAuditLog['entityType'],
612|    entityId: string,
613|    action: FreightAuditLog['action'],
614|    changes: FreightAuditLog['changes'],
615|    entitySnapshot: Record<string, unknown>,
616|    version?: number
617|  ) => {
618|    if (!user) return;
619|
620|    try {
621|      const log = {
622|        entity_type: entityType,
623|        entity_id: entityId,
624|        action,
625|        changes,
626|        entity_snapshot: entitySnapshot,
627|        changed_by: user.id,
628|        changed_by_username: user.username,
629|        changed_by_name: user.name,
630|        version,
631|      };
632|
633|      const { error } = await supabase
634|        .from(TABLES.AUDIT_LOGS)
635|        .insert(log);
636|
637|      if (error) {
638|        handleError(error, 'Audit Log 생성');
639|        return;
640|      }
641|
642|      // Directly update the audit logs state instead of reloading
643|      const { data } = await supabase
644|        .from(TABLES.AUDIT_LOGS)
645|        .select('*')
646|        .eq('entity_id', entityId)
647|        .eq('action', action)
648|        .order('created_at', { ascending: false })
649|        .limit(1)
650|        .single();
651|
652|      if (data) {
653|        const newLog: FreightAuditLog = {
654|          id: data.id,
655|          entityType: data.entity_type,
656|          entityId: data.entity_id,
657|          action: data.action,
658|          changes: data.changes,
659|          entitySnapshot: data.entity_snapshot,
660|          changedBy: data.changed_by,
661|          changedByUsername: data.changed_by_username,
662|          changedByName: data.changed_by_name,
663|          timestamp: data.created_at,
664|          version: data.version,
665|        };
666|        setAuditLogs(prev => [newLog, ...prev]);
667|      }
668|    } catch (error) {
669|      handleError(error, 'Audit Log 생성');
670|    }
671|  }, [user]); // Only depend on user
672|
673|  // Helper function to detect changes between old and new entity
674|  const detectChanges = (oldEntity: Record<string, unknown> | null, newEntity: Record<string, unknown>): FreightAuditLog['changes'] => {
675|    const changes: FreightAuditLog['changes'] = [];
676|    const keys = new Set([...Object.keys(oldEntity || {}), ...Object.keys(newEntity || {})]);
677|    
678|    keys.forEach(key => {
679|      if (key === 'id' || key === 'createdAt' || key === 'updatedAt') return;
680|      
681|      const oldValue = oldEntity?.[key];
682|      const newValue = newEntity?.[key];
683|      
684|      if (JSON.stringify(oldValue) !== JSON.stringify(newValue)) {
685|        changes.push({
686|          field: key,
687|          oldValue: oldValue as string | number | boolean | undefined,
688|          newValue: newValue as string | number | boolean | undefined,
689|        });
