863|  // Get next version number for sea freight
864|  const getSeaFreightVersion = (carrier: string, pol: string, pod: string, excludeId?: string): number => {
865|    const sameGroup = seaFreights.filter(f => 
866|      f.carrier === carrier && 
867|      f.pol === pol && 
868|      f.pod === pod &&
869|      f.id !== excludeId
870|    );
871|    if (sameGroup.length === 0) return 1;
872|    const maxVersion = Math.max(...sameGroup.map(f => f.version || 1));
873|    return maxVersion + 1;
874|  };
875|
876|  // Get next version number for agent sea freight
877|  const getAgentSeaFreightVersion = (agent: string, pol: string, pod: string, excludeId?: string): number => {
878|    const sameRoute = agentSeaFreights.filter(f => 
879|      f.agent === agent && 
880|      f.pol === pol && 
881|      f.pod === pod &&
882|      f.id !== excludeId
883|    );
884|    if (sameRoute.length === 0) return 1;
885|    const maxVersion = Math.max(...sameRoute.map(f => f.version || 1));
886|    return maxVersion + 1;
887|  };
