673|  // Helper function to detect changes between old and new entity
674|  const detectChanges = (oldEntity: Record<string, unknown> | null, newEntity: Record<string, unknown>): FreightAuditLog['changes'] => {
675|    const changes: FreightAuditLog['changes'] = [];
676|    const keys = new Set([...Object.keys(oldEntity || {}), ...Object.keys(newEntity || {})]);
677|    
678|    keys.forEach(key => {
679|      if (key === 'id' || key === 'createdAt' || key === 'updatedAt') return;
680|      
681|      const oldValue = oldEntity?.[key];
682|      const newValue = newEntity?.[key];
683|      
684|      if (JSON.stringify(oldValue) !== JSON.stringify(newValue)) {
685|        changes.push({
686|          field: key,
687|          oldValue: oldValue as string | number | boolean | undefined,
688|          newValue: newValue as string | number | boolean | undefined,
689|        });
690|      }
691|    });
692|    
693|    return changes;
694|  };
